class book_show_more : Fragment(), AdaptorBook.ItemEventsBook {



    lateinit var binding: FragmentBookShowMoreBinding


    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {

        binding = FragmentBookShowMoreBinding.inflate(layoutInflater)
        return binding.root

