    override fun onItemClickedBook(dataBook: DataBook) {

        val book_bundel = Bundle()
        book_bundel.putParcelable("dataBook", dataBook)
        findNavController().navigate(R.id.action_book_show_more_to_book_page, book_bundel)
    }
